import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFormContext } from '../../context/FormContext';
import './formStudent.css';
import { IStudent } from '../../types/student';
import BtnNavigate from '../btnNavigate/BtnNavigate';
import { ROUTES } from '../../constants/constUrls';
import { validateForm } from '../../utils/validateForm';
import useCities from '../../hooks/useCities';
import { ICity } from '../../types/city';

const FormStudent: React.FC = () => {
    const { addStudent } = useFormContext();
    const cities = useCities();
    const navigate = useNavigate();
    const [formData, setFormData] = useState<IStudent>({
        firstName: '',
        lastName: '',
        dateOfBirth: '',
        israeliID: '',
        cityID: 1
    });
    const [cityName, setCityName] = useState<string>('');
    const [errors, setErrors] = useState({
        firstName: '',
        lastName: '',
        dateOfBirth: '',
        israeliID: '',
        city: ''
    });
    const handleSelected = (city: ICity) => {
        setCityName(city.cityName)
    }
    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = e.target;

        if (name === 'israeliID' && !/^\d*$/.test(value)) {
            setErrors(prevErrors => ({
                ...prevErrors,
                israeliID: 'תעודת זהות יכולה להכיל רק מספרים'
            }));
            return;
        }

        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));

        setErrors(prevErrors => ({
            ...prevErrors,
            [name]: ''
        }));
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        const { valid, errors } = validateForm(formData);
        setErrors(errors);

        if (valid) {
            addStudent(formData);
            navigate(ROUTES.USER_LIST);
        }
    };

    return (
        <div className="form-container">
            <h1>טופס הרשמה</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="firstName">שם פרטי:</label>
                    <input
                        type="text"
                        id="firstName"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                    />
                    {errors.firstName && <span className="error-message">{errors.firstName}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="lastName">שם משפחה:</label>
                    <input
                        type="text"
                        id="lastName"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                    />
                    {errors.lastName && <span className="error-message">{errors.lastName}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="dateOfBirth">תאריך לידה:</label>
                    <input
                        type="date"
                        id="dateOfBirth"
                        name="dateOfBirth"
                        value={formData.dateOfBirth}
                        onChange={handleChange}
                    />
                    {errors.dateOfBirth && <span className="error-message">{errors.dateOfBirth}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="israeliID">תעודת זהות:</label>
                    <input
                        type="text"
                        id="israeliID"
                        name="israeliID"
                        value={formData.israeliID}
                        onChange={handleChange}
                    />
                    {errors.israeliID && <span className="error-message">{errors.israeliID}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="city">עיר:</label>
                    <select
                        id="city"
                        name="city"
                        value={cityName}
                        onChange={handleChange}
                    >
                        {cities?.map((city, index) => (
                            <option onClick={() => handleSelected(city)} key={index} value={city.cityName}>
                                {city.cityName}
                            </option>
                        ))}
                    </select>
                    {errors.city && <span className="error-message">{errors.city}</span>}
                </div>
                <button type="submit" className="submit-button">שלח</button>
                <BtnNavigate
                    text='לרשימה המלאה'
                    url={ROUTES.USER_LIST}
                />
            </form>
        </div>
    );
};

export default FormStudent;
